import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import "../../styles/ContactFormStyles.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function ContactForm() {
  const [isEmailSent, setIsEmailSent] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
    solution: '', // Nouveau champ pour la solution
  });

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const sendEmail = (e) => {
    e.preventDefault();

    if (!emailRegex.test(formData.email)) {
      toast.error("Veuillez saisir une adresse e-mail valide", {
        position: "bottom-right",
        autoClose: 4000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
      return;
    }

    emailjs.sendForm('service_4ky1shj', 'template_6qdcrqm', e.target, 'FP3hLqb5PwWP6s7Y9')
      .then((result) => {
        setIsEmailSent(true);
        setFormData({
          name: '',
          email: '',
          subject: '',
          message: '',
          solution: '', // Réinitialiser le champ solution après l'envoi
        });

        toast.success("Votre message a été envoyé avec succès !", {
          position: "bottom-right",
          autoClose: 6000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });

        console.log(result.text);
      })
      .catch((error) => {
        console.log(error.text);
      });
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div id="contact" className="from-container">
      <div className="blog__top-content">
        <h6 className="subtitle">Contactez Nous</h6>
        <h2>
          Demandez nous un
          <span className="highlight"> Devis</span>
        </h2>
      </div>
      <h4 className='txt-cf'>Nous sommes ravis de vous accompagner dans la réalisation de vos projets web. </h4>
      <h4 className='txt-cf'>N'hésitez pas à nous contacter pour discuter de vos besoins et découvrir comment EnideWeb peut concrétiser votre vision en ligne.</h4>
      <form onSubmit={(e) => { sendEmail(e); }}>
        <input
          type="text"
          name="name"
          placeholder="Nom"
          value={formData.name}
          onChange={handleInputChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={formData.email}
          onChange={handleInputChange}
          required
        />
        <input
          type="text"
          name="subject"
          placeholder="Sujet"
          value={formData.subject}
          onChange={handleInputChange}
          required
        />

          <select
          name="solution"
          value={formData.solution}
          onChange={handleInputChange}
          required
        >
          <option value="" disabled>Sélectionnez une solution</option>
          <option value="Pack Essentiel">Pack Essentiel</option>
          <option value="Pack Entreprise">Pack Entreprise</option>
          <option value="Pack E-Commerce">Pack E-Commerce</option>
        </select>
        <textarea 
          name="message"
          placeholder=" Écrivez votre message ici. Incluez toutes les informations pertinentes. 
          Adresse : 
          Téléphone : 
          E-mail :"
          rows="8"
          value={formData.message}
          onChange={handleInputChange}
          required
        />

        <button type="submit">Envoyer votre Message</button>
        <ToastContainer />
      </form>
    </div>
  );
}

export default ContactForm;
