import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import "../../styles/ContactFormStyles.css";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

// ... (autres imports)

function ContactForm() {
  const [isEmailSent, setIsEmailSent] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: '',
    solution: '', // Nouveau champ pour la solution
  });

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

  const sendEmail = (e) => {
    e.preventDefault();

    if (!emailRegex.test(formData.email)) {
      toast.error("Veuillez saisir une adresse e-mail valide", {
        position: "bottom-right",
        autoClose: 4000,
        hideProgressBar: false,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        theme: "colored",
      });
      return;
    }

    emailjs.sendForm('service_u1ud7yk', 'template_kjhqckn', e.target, 'KOETGARUQKuhvP3ba')
      .then((result) => {
        setIsEmailSent(true);
        setFormData({
          name: '',
          email: '',
          subject: '',
          message: '',
          solution: '', // Réinitialiser le champ solution après l'envoi
        });

        toast.success("Votre message a été envoyé avec succès !", {
          position: "bottom-right",
          autoClose: 6000,
          hideProgressBar: false,
          closeOnClick: true,
          pauseOnHover: true,
          draggable: true,
          progress: undefined,
          theme: "colored",
        });

        console.log(result.text);
      })
      .catch((error) => {
        console.log(error.text);
      });
  };

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div id="contact" className="from-container">
      <div className="blog__top-content">
        <h6 className="subtitle">Contactez Nous</h6>
        <h2>
          Demandez nous un
          <span className="highlight"> Devis</span>
        </h2>
      </div>
      <h4 className='txt-cf'>Nous sommes ravis de vous accompagner dans la réalisation de vos projets web. </h4>
      <h4 className='txt-cf'>N'hésitez pas à nous contacter pour discuter de vos besoins et découvrir comment EnideWeb peut concrétiser votre vision en ligne.</h4>
      <form onSubmit={(e) => { sendEmail(e); }}>
        {/* ... autres champs ... */}
        <button type="submit">Envoyer votre Message</button>
        <ToastContainer />
      </form>
    </div>
  );
}

export default ContactForm;
